DROP TABLE IF EXISTS recipe;
BEGIN TRANSACTION;
CREATE TABLE recipe (uuid UUID DEFAULT gen_random_uuid() PRIMARY KEY, title VARCHAR(200) NOT NULL, label VARCHAR(20) NOT NULL, description TEXT NOT NULL, prep_time_mins INTEGER NOT NULL, recipe_url TEXT NOT NULL, image_url TEXT NOT NULL, image_url_thumbnail TEXT NOT NULL, created_at timestamp default current_timestamp);
COMMIT;

select * from recipe;